def convert_to_uppercase_and_count(s):
    count = 1
    uppercase_s = ''
    for char in s:
        if 97 <= ord(char) <= 122:
            uppercase_s += chr(ord(char) - 32)
            count += 1
        else:
            uppercase_s += char
    return (uppercase_s, count)

