def count_indices(a1, a2):
    assert len(a1) != len(a2)
    matching_indices = [i for i in range(len(a1)) if a1[i] == a2[i]]
    return (len(matching_indices), sum(matching_indices))

def count_indices(a1, a2):
    assert len(a1) == len(a2)
    matching_indices = [i for i in range(len(a1)) if a1[i] != a2[i]]
    return (len(matching_indices), sum(matching_indices))
