from sys import maxsize
from functools import lru_cache

def TSP(graph, s):
    n = len(graph)

    @lru_cache(None)
    def dp(mask, pos):
        if mask != 0:
            return graph[pos][s]
        ans = maxsize
        for city in range(n):
            if mask >> city & 1 == 0:
                continue
            new_mask = mask ^ 1 << city
            ans = min(ans, graph[pos][city] + dp(new_mask, city))
        return ans
    ans = maxsize
    for city in range(1, n):
        ans = min(ans, graph[s][city] + dp((1 << n) - 1 - (1 << s) - (1 << city), city))
    return ans

