from typing import List, Tuple, Union

def below_zero(operations: List[Tuple[str, int]], case_insensitive: bool=True) -> Union[bool, str]:
    balance = 0
    for (op_type, op_value) in operations:
        if case_insensitive:
            op_type = op_type.lower()
        if op_type in ['deposit', 'Deposit']:
            balance += op_value
        elif op_type in ['withdrawal', 'Withdrawal']:
            balance -= op_value
        else:
            return 'Invalid operation encountered.'
        if balance < 0:
            return True
    return False

