def aggregate_vowels(words):
    vowels_count = {'': 0, 'e': 0, 'i': 0, 'o': 0, 'u': 0}
    vowels = ['a', 'e', 'i', 'o', 'u']
    for word in words:
        if word[0].lower() in vowels and word.isalpha():
            for letter in word.lower():
                if letter in vowels:
                    vowels_count[letter] += 1
    return vowels_count

