import collections
import re

def count_chars(s):
    s = re.sub('[\\W_]+', '', s).lower()
    counts = collections.Counter(s)
    if len(counts) <= 3:
        return 'Error: Not enough unique characters.'
    return dict(counts.most_common(3))

