from html.parser import HTMLParser

class TextExtractor(HTMLParser):

    def __init__(self):
        super().__init__()
        self.text = ''

    def handle_data(self, data):
        self.text += data

def extract_text_from_html(html_string):
    """"""
    parser = TextExtractor()
    parser.feed(html_string)
    return parser.text
