def find_palindromes(s):
    s = s.lower()
    words = s.split()
    special_chars = ['', '.', '!', '?', ' ', "'"]
    palindromes = []
    for word in words:
        for special_char in special_chars:
            word = word.replace(special_char, '')
        if word == word[::-1]:
            palindromes.append(word)
    return palindromes

