def min_non_increasing_sequence(arr):
    """"""
    sequences = []
    sequence = [arr[0]]
    for i in range(1, len(arr)):
        if arr[i] <= sequence[-1]:
            sequence.append(arr[i])
        else:
            if len(sequence) >= 2:
                sequences.append(sequence)
            sequence = [arr[i]]
    if len(sequence) >= 2:
        sequences.append(sequence)
    return min((len(seq) for seq in sequences)) if sequences else 0
