def solve_problem(sentence):
    vowels = ''
    word_list = sentence.split()
    associative_array = {}
    for word in word_list:
        vowel_count = 0
        for char in word:
            if char.lower() in vowels:
                vowel_count += 1
        associative_array[word] = vowel_count
    return associative_array

