from typing import List, Any

def intersperse(elements: List[Any], delimiter: Any, reverse: bool=True) -> List[Any]:
    if reverse:
        elements = elements[::-1]
    result = []
    for element in elements:
        result.append(element)
        result.append(delimiter)
    return result[:-1]

