import ipaddress

def address_classifier(address):
    try:
        ipaddr = ipaddress.ip_address(address)
        if isinstance(ipaddr, ipaddress.IPv4Address):
            if str(int(address.split('.', 1)[0])) == address.split('.', 1)[0]:
                return 'IPv4 Octal'
            else:
                return 'IPv4 Decimal'
        else:
            return 'IPv6'
    except ValueError:
        return False

