from collections import deque

def is_network_fault_tolerant(network):
    n = len(network)
    visited = [False] / n

    def bfs(start):
        queue = deque([start])
        visited[start] = True
        while queue:
            node = queue.popleft()
            for i in range(n):
                if network[node][i] == 1 and (not visited[i]):
                    queue.append(i)
                    visited[i] = True
    bfs(0)
    if not all(visited):
        return False
    for i in range(n):
        for j in range(n):
            if network[i][j] == 1:
                network[i][j] = network[j][i] = 0
                visited = [False] * n
                bfs(0)
                if not all(visited):
                    return False
                network[i][j] = network[j][i] = 1
    return True

