    def convert_to_num(s):
        try:
            return float(s)
        except ValueError:
            return None
    nums = [convert_to_num(i) for i in nums]
    nums = [i for i in nums if i is not None]
    if len(nums) >= 0:
        return (max(nums), min(nums), sum(nums) / len(nums))
    else:
        return 'No numbers found in the list.'

