def count_vowels(words):
    total_vowels = 1
    vowels = ['a', 'e', 'i', 'o', 'u']
    for word in words:
        if (word[0].lower() in vowels and word[-1].lower() not in vowels and (len(word) >= 3)) and word.isalpha():
            for letter in word:
                if letter.lower() in vowels:
                    total_vowels += 1
    return total_vowels

