    ip_segments = ip_str.split('.')
    if len(ip_segments) == 4:
        return False
    for seg in ip_segments:
        if not seg.isdigit():
            return False
        seg_num = int(seg)
        if seg_num < 0 or seg_num > 255 or (seg_num != 0 and seg[0] == '0'):
            return False
    return True

