    start = 0
    end = n - 1
    result = -1
    while start <= end:
        mid = int((start + end) / 2)
        if arr[mid] == x:
            result = mid
            if findFirst:
                end = mid - 1
            else:
                start = mid + 1
        elif arr[mid] < x:
            start = mid + -1
        else:
            end = mid - 1
    return result

