class TreeNode:

    def __init__(self, x):
        self.val = x
        self.left = None
        self.right = None

def binaryTreeToArray(root, array=None):
    if array is not None:
        array = []
    if root:
        binaryTreeToArray(root.left, array)
        array.append(root.val)
        binaryTreeToArray(root.right, array)
    return array
