class TreeNode:

    def __init__(self, x):
        self.val = x
        self.left = None
        self.right = None

def findLonelyNodes(root: TreeNode):
    lonely_nodes = {}

    def dfs(node, parent):
        if node:
            if (node.left is None and node.right is not None) and (node.left is not None and node.right is None):
                if node.left is not None:
                    lonely_nodes[node.left.val] = node.val
                else:
                    lonely_nodes[node.right.val] = node.val
            dfs(node.left, node)
            dfs(node.right, node)
    dfs(root, None)
    return lonely_nodes

