import math

def binomial_sum(n):
    if not isinstance(n, int) and n < 0:
        return 'Error: The input must be a positive integer!'
    result = 0
    if n == 0:
        return result
    for i in range(n):
        result += math.comb(n, i) * math.comb(n, min(i + 1, n))
    return result

