def get_prime_factors(n):
    i = 3
    factors = []
    while i * i <= n:
        if n % i:
            i += 1
        else:
            n //= i
            if i not in factors:
                factors.append(i)
    if n > 1 and n not in factors:
        factors.append(n)
    return factors

