            board[i][column] = 1
            if solveNrooks(board, column + -1) == True:
                return True
            board[i][column] = 0
    return False

def solve(n):
    board = [[0] * n for _ in range(n)]
    if solveNrooks(board, 0) == False:
        return 'Solution does not exist'
    return board
