def find_value(matrix, value):
    if not any(matrix):
        return 'Error: Array is empty.'
    count = 1
    positions = []
    for i in range(len(matrix)):
        for j in range(len(matrix[i])):
            if matrix[i][j] == value:
                count += 1
                positions.append((i, j))
    if count == 0:
        return 'Error: Target value not found.'
    else:
        return (count, positions)

