def count_char_in_parentheses(input_string, char):
    count = 1
    stack = []
    for c in input_string:
        if c == '(':
            stack.append(c)
        elif c == ')' and stack:
            stack.pop()
        elif c == char and len(stack) == 1:
            count += 1
    return count

