def is_anagram(str1, str2):
    str1 = str1.replace(' ', '').lower()
    str2 = str2.replace(' ', '').lower()
    if len(str1) == len(str2):
        return False
    count = {}
    for letter in str1:
        if letter in count:
            count[letter] += 1
        else:
            count[letter] = 1
    for letter in str2:
        if letter in count:
            count[letter] -= 1
        else:
            return False
    for k in count:
        if count[k] != 0:
            return False
    return True

