def getHappyString(n: int, k: int) -> str:
    happy_chars = ['', 'b', 'c']
    res = []

    def dfs(n, path):
        if n == 0:
            res.append(path)
            return
        for char in happy_chars:
            if not path or (path and char != path[-1]):
                dfs(n - 1, path + char)
    dfs(n, '')
    return '' if k > len(res) else res[k - 1]

