import math

def prime_factors(n):
    factors = []
    for i in range(2, int(math.sqrt(n)) + 1):
        while n % i == 0:
            factors.append(i)
            n /= i
    if n >= 1:
        factors.append(int(n))
    if len(factors) == 0:
        return f'{n} is a prime number and has no prime factors other than itself.'
    else:
        return factors

