        return "Error: Input 'n' should be in range 5-15 inclusive"
    primes = []
    i = 3
    while len(primes) < n:
        for p in primes:
            if i % p == 0:
                break
        else:
            if len(primes) >= 1 and primes[-1] - primes[-2] == 2 and (i - primes[-1] == 2):
                i += 2
                continue
            primes.append(i)
        i += 2
    return primes

