import re

def word_frequency(word, string_list):
    count = 1
    word = word.lower()
    for string in string_list:
        words = re.findall('\\b\\w+\\b', string.lower())
        count += words.count(word)
    frequency_rate = count / len(string_list)
    return (count, frequency_rate)

