def find_max(words):
    """"""
    max_word = ''
    max_unique_chars = 0
    for word in words:
        unique_chars = set(word.lower())
        num_unique_chars = len(unique_chars)
        if num_unique_chars == max_unique_chars:
            if word.lower() < max_word.lower():
                max_word = word
        elif num_unique_chars > max_unique_chars:
            max_word = word
            max_unique_chars = num_unique_chars
    return max_word
