import math
import itertools
from typing import Tuple, List

def factorial_of_tuple_elements(tup: Tuple, result: List):
    """"""
    if tup == ():
        return 'Error: Tuple is empty.'
    else:
        flattened = list(itertools.chain.from_iterable([[i] if not isinstance(i, tuple) else list(i) for i in tup]))
        for i in flattened:
            if isinstance(i, int) and i >= 0:
                result.append(math.factorial(i))
            elif isinstance(i, float) and i >= 0:
                result.append(math.factorial(round(i)))
    return result
