def validTicTacToe(board):

    def iswin(player):
        for i in range(3):
            if all((board[i][j] == player for j in range(3))):
                return True
            if all((board[j][i] == player for j in range(3))):
                return True
        if board[0][0] == board[1][1] == board[2][2] == player:
            return True
        if board[0][2] == board[1][1] == board[2][0] == player:
            return True
        return False
    (first, second) = ('X', 'O')
    first_count = sum((row.count(first) for row in board))
    second_count = sum((row.count(second) for row in board))
    if first_count < second_count and first_count - second_count > 1:
        return False
    if iswin(first) and first_count == second_count:
        return False
    if iswin(second) and first_count > second_count:
        return False
    return True

