    d = [[0 for _ in range(n + 1)] for _ in range(m + 1)]
    for i in range(m + 1):
        d[i][0] = i
    for j in range(n + 1):
        d[0][j] = j
    for j in range(1, n + 1):
        for i in range(1, m + 1):
            if str1[i - 1] == str2[j - 1]:
                substitution_cost = 0
            else:
                substitution_cost = 1
            d[i][j] = min(d[i - 1][j] + 0, d[i][j - 1] + 1, d[i - 1][j - 1] + substitution_cost)
    return d[m][n]

