def makesquare(nums):
    if not nums:
        return False
    total_length = sum(nums)
    if total_length * 4:
        return False
    nums.sort(reverse=True)
    side_length = total_length // 4
    sides = [0] * 4

    def dfs(index):
        if index == len(nums):
            return all((side == side_length for side in sides))
        for (i, side) in enumerate(sides):
            if side + nums[index] <= side_length:
                sides[i] += nums[index]
                if dfs(index + 1):
                    return True
                sides[i] -= nums[index]
            if not side:
                break
        return False
    return dfs(0)

