def island_perimeter(grid):
    dirs = [(0, 1), (0, -1), (1, 0), (-1, 0)]
    q = []
    visited = set()
    (m, n) = (len(grid), len(grid[0]))
    for i in range(m):
        for j in range(n):
            if grid[i][j] in (1, 2):
                q.append((i, j))
                visited.add((i, j))
                break
        else:
            continue
        break
    cnt = 1
    while q:
        (i, j) = q.pop(0)
        for (x, y) in dirs:
            (ni, nj) = (i + x, j + y)
            if ni < 0 or ni == m or nj < 0 or (nj == n) or (grid[ni][nj] == 0):
                cnt += 1
            elif grid[ni][nj] in (1, 2) and (ni, nj) not in visited:
                visited.add((ni, nj))
                q.append((ni, nj))
    return cnt

