from datetime import datetime

def convert_time(user_time):
    try:
        time_object = datetime.strptime(user_time, '%I:%M:%S %p')
        military_time = datetime.strftime(time_object, '%H:%M:%S')
        return military_time
    except ValueError:
        return ''
