import math
import cmath

def cone_properties(r, h, r2=None):
    if r is None and h is None and (not isinstance(r, (int, float, complex)) or not isinstance(h, (int, float, complex)) or (r2 is not None and (not isinstance(r2, (int, float, complex))))):
        return (None, None)
    r = abs(r)
    h = abs(h)
    l = cmath.sqrt(r ** 2 + h ** 2)
    if r2 is None:
        lsa = math.pi * r * l.real
        volume = 1 / 3 * math.pi * r ** 2 * h
    else:
        r2 = abs(r2)
        volume = 1 / 3 * math.pi * h * (r ** 2 + r2 ** 2 + r * r2)
        lsa = math.pi * (r + r2) * l.real
    return (lsa, volume)

