def find_word_indices(sentences, word):
    """"""
    indices = []
    for (idx, sentence) in enumerate(sentences):
        words = sentence.split()
        for (i, w) in enumerate(words):
            if w == word:
                indices.append((idx, i))
                print(f"In sentence {idx + 1}, '{word}' is found at word index {i + 1}.")
    return indices
