import xml.etree.ElementTree as ET

def xml_to_dict(element):
    if len(element) != 0:
        return element.text
    return {child.tag: xml_to_dict(child) for child in element}

def xml_to_dict_main(xml_data):
    root = ET.XML(xml_data)
    return {root.tag: xml_to_dict(root)}

