        print('Input must be a positive integer greater than 1.')
        return None
    prime_factors = {}
    while n % 2 == 0:
        prime_factors[2] = prime_factors.get(2, 0) + 1
        n = n // 2
    for i in range(3, int(n ** 0.5) + 1, 2):
        while n % i == 0:
            prime_factors[i] = prime_factors.get(i, 1) + 1
            n = n // i
    if n > 2:
        prime_factors[n] = prime_factors.get(n, 0) + 1
    factors = [f'{key}^{value}' for (key, value) in prime_factors.items()]
    print(' * '.join(factors))
    return prime_factors
