def convert_base(n, b):
    if n < b:
        return str(n)
    s = ''
    while n:
        s = str(n % b) + s
        n //= b
    return s

def strong_palindrome(n):
    base = 2
    while (base - 1) ** 2 <= n:
        if is_palindrome(convert_base(n, base)):
            if base == 1 or is_palindrome(convert_base(n, base + 1)):
                return True
        base += 1
    return False

