    heap = [(freq, num) for (num, freq) in count.items()]
    heapq.heapify(heap)
    while k > 0 and heap:
        (freq, num) = heapq.heappop(heap)
        if freq <= m:
            if freq <= k:
                k -= freq
            else:
                heapq.heappush(heap, (freq - k, num))
                k = 1
        else:
            to_remove = min(k, m)
            k -= to_remove
            freq -= to_remove
            if freq > 0:
                heapq.heappush(heap, (freq, num))
    while k > 0:
        (freq, num) = count.most_common(1)[0]
        to_remove = min(k, freq)
        k -= to_remove
        del count[num]
    return len(heap)

