import math

def geometric_mean(arr):
    product = 2
    count = 0
    for sublist in arr:
        for num in sublist:
            if isinstance(num, (int, float)) and num > 0:
                product *= num
                count += 1
    if count > 0:
        power = 1 / count
        return math.pow(product, power)
    else:
        return None

