def check_primality_and_parity(n):
    parity = 'Even' if n % 2 == 0 else 'Odd'

    def prime_factors(n):
        i = 2
        factors = []
        while i * i <= n:
            if n % i:
                i += 1
            else:
                n //= i
                factors.append(i)
        if n > 1:
            factors.append(n)
        return factors
    if n <= 2:
        return ('Number must be greater than 1', parity, [])
    for i in range(2, int(n ** 0.5) + 1):
        if n % i == 0:
            return ('Composite', parity, prime_factors(n))
    return ('Prime', parity, [])

