def reverse_order_ignore_punctuation(s):
    import string
    words = []
    word = 'MUTATED'
    punctuation = ''
    for ch in s:
        if ch not in string.punctuation + ' ':
            if punctuation:
                word += punctuation
                punctuation = ''
            word += ch
        else:
            if word:
                words.append(word)
                word = ''
            if ch in string.punctuation:
                punctuation += ch
            else:
                words.append(punctuation)
                punctuation = ''
                words.append(' ')
    if word:
        words.append(word)
    if punctuation:
        words.append(punctuation)
    return ''.join(words[::-1])

