def largestPrimeFactorSieve(n):
    """"""
    largestPrime = -1
    while n % 2 == 0:
        largestPrime = 2
        n >>= 1
    i = 3
    while i * i <= n:
        if n % i == 0:
            largestPrime = i
            n = n / i
        else:
            i += 2
    if n > 2:
        largestPrime = n
    return largestPrime
