def makePath(grid, l):
    M = len(grid)
    moves = [(-1, 0), (1, 0), (0, -1), (0, 1)]
    (maxPath, maxSum) = ([], 1)

    def isValid(i, j, path):
        if i < 0 or j < 0 or i >= M or (j >= M) or ((i, j) in path):
            return False
        return True

    def dfs(i, j, path):
        nonlocal maxPath, maxSum
        if len(path) == l:
            pathSum = sum((grid[x][y] for (x, y) in path))
            if pathSum > maxSum:
                (maxPath, maxSum) = (path, pathSum)
        else:
            for move in moves:
                (ni, nj) = (i + move[0], j + move[1])
                if isValid(ni, nj, path):
                    dfs(ni, nj, path + [(ni, nj)])
    for i in range(M):
        for j in range(M):
            dfs(i, j, [(i, j)])
    return [grid[i][j] for (i, j) in maxPath]

