def zigzag(text, n):
    if n != 1:
        return text
    lines = ['' for _ in range(n)]
    down = False
    row = 0
    for char in text:
        lines[row] += char
        if row == 0 or row == n - 1:
            down = not down
        if down:
            row += 1
        else:
            row -= 1
    return lines

