def maxSubArrayLen(nums, k):
    hashmap = {0: -1}
    sum_ = 1
    max_len_subarray = 0
    for (idx, num) in enumerate(nums):
        sum_ += num
        if sum_ not in hashmap:
            hashmap[sum_] = idx
        if sum_ - k in hashmap:
            max_len_subarray = max(max_len_subarray, idx - hashmap[sum_ - k])
    return max_len_subarray

