def polite_numbers(n):
    """"""
    if n < 1:
        return 'Error: input should be a positive integer'
    nth_polite_number = 0
    polite_number_sum = 0
    nth_polite_count = 0
    i = 1
    while nth_polite_count < n:
        if i & i - 1:
            nth_polite_number = i
            polite_number_sum += nth_polite_number
            nth_polite_count += 1
        i += 1
    return (nth_polite_number, polite_number_sum)
